            380 => 
            array (
                'id' => 8886,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 15,
                'contenido' => 'Ahimaas en Neftalí; éste tomó también por esposa a Basemat, hija de Salomón.',
                'referencia' => '1 Reyes 4:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
            ),
            381 => 
            array (
                'id' => 8887,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 16,
                'contenido' => 'Baana hijo de Husai, en Aser y en Alot;',
                'referencia' => '1 Reyes 4:16',
                'imagen' => '',
