            112 => 
            array (
                'id' => 9118,
                'capitulo_id' => 301,
                'pericopa_id' => 668,
                'num_versiculo' => 12,
                'contenido' => 'Y de la madera de sándalo hizo el rey balaustres para la casa de Jehová y para la casa del rey, arpas también y salterios para los cantores. Nunca vino tanta madera de sándalo, ni se ha visto hasta hoy.',
                'referencia' => '1 Reyes 10:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
