            array (
                'id' => 9134,
                'capitulo_id' => 301,
                'pericopa_id' => 670,
                'num_versiculo' => 28,
                'contenido' => 'E importaban caballos de Egipto y de Coa para Salomón, porque los mercaderes del rey los compraban allí.',
