            array (
                'id' => 9348,
                'capitulo_id' => 308,
                'pericopa_id' => 688,
                'num_versiculo' => 4,
                'contenido' => 'Y sucederá que beberás del arroyo, y yo he mandado a los cuervos que te den allí de comer.',
