            421 => 
            array (
                'id' => 10923,
                'capitulo_id' => 356,
                'pericopa_id' => 810,
                'num_versiculo' => 5,
                'contenido' => 'Y llegaron los sirios de Damasco en ayuda de Hadar-ezer, rey de Soba; y David mató a veintidós mil hombres de los sirios.',
                'referencia' => '1 Crónicas 18:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:15',
