            422 => 
            array (
                'id' => 10924,
                'capitulo_id' => 356,
                'pericopa_id' => 810,
                'num_versiculo' => 6,
                'contenido' => 'Y puso David guarnición en Siria de Damasco; y los sirios fueron hechos siervos de David, sujetos a tributo, porque Jehová guardaba a David dondequiera que iba.',
                'referencia' => '1 Crónicas 18:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:15',
                'updated_at' => '2024-03-12 15:25:15',
            ),
            423 => 
            array (
                'id' => 10925,
                'capitulo_id' => 356,
                'pericopa_id' => 810,
                'num_versiculo' => 7,
                'contenido' => 'Tomó también David los escudos de oro que llevaban los siervos de Hadar-ezer, y los llevó a Jerusalén.',
                'referencia' => '1 Crónicas 18:7',
                'imagen' => '',
                'pie_imagen' => '',
