            176 => 
            array (
                'id' => 11178,
                'capitulo_id' => 366,
                'pericopa_id' => 823,
                'num_versiculo' => 7,
                'contenido' => 'Asimismo yo estableceré su reino para siempre si él se esfuerza en guardar mis mandamientos y mis decretos, como en este día.',
                'referencia' => '1 Crónicas 28:7',
