            354 => 
            array (
                'id' => 11356,
                'capitulo_id' => 374,
                'pericopa_id' => 834,
                'num_versiculo' => 4,
                'contenido' => 'Entonces el rey y todo el pueblo ofrecieron sacrificios delante de Jehová.',
