            array (
                'id' => 11444,
                'capitulo_id' => 378,
                'pericopa_id' => 841,
                'num_versiculo' => 2,
                'contenido' => 'Pero vino la palabra de Jehová a Semaías, hombre de Dios, diciendo:',
