            55 => 
            array (
                'id' => 11557,
                'capitulo_id' => 384,
                'pericopa_id' => 849,
                'num_versiculo' => 6,
                'contenido' => 'Y se animó su corazón en los caminos de Jehová, y quitó de Judá los lugares altos y las imágenes de Asera.',
                'referencia' => '2 Crónicas 17:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
