            56 => 
            array (
                'id' => 11558,
                'capitulo_id' => 384,
                'pericopa_id' => 849,
                'num_versiculo' => 7,
                'contenido' => 'Al tercer año de su reinado envió a sus oficiales Ben-hail, y Abdías, y Zacarías, y Natanael y Micaías, para que enseñasen en las ciudades de Judá;',
                'referencia' => '2 Crónicas 17:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
            ),
            57 => 
            array (
                'id' => 11559,
                'capitulo_id' => 384,
                'pericopa_id' => 849,
                'num_versiculo' => 8,
                'contenido' => 'y con ellos a los levitas Semaías, y Netanías, y Zebadías, y Asael, y Semiramot, y Jonatán, y Adonías, y Tobías y Tobadonías, levitas todos; y con ellos a los sacerdotes Elisama y Joram.',
                'referencia' => '2 Crónicas 17:8',
