            72 => 
            array (
                'id' => 11574,
                'capitulo_id' => 385,
                'pericopa_id' => 850,
                'num_versiculo' => 4,
                'contenido' => 'Y dijo Josafat al rey de Israel: Te ruego que consultes hoy la palabra de Jehová.',
                'referencia' => '2 Crónicas 18:4',
