            118 => 
            array (
                'id' => 12120,
                'capitulo_id' => 405,
                'pericopa_id' => 890,
                'num_versiculo' => 65,
                'contenido' => 'sin contar sus siervos y sus siervas, los cuales eran siete mil trescientos treinta y siete; y tenían doscientos cantores y cantoras.',
                'referencia' => 'Esdras 2:65',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
