            207 => 
            array (
                'id' => 12209,
                'capitulo_id' => 410,
                'pericopa_id' => 896,
                'num_versiculo' => 8,
                'contenido' => 'Y llegó a Jerusalén en el mes quinto del año séptimo del rey.',
