            ),
            371 => 
            array (
                'id' => 12373,
                'capitulo_id' => 416,
                'pericopa_id' => 903,
                'num_versiculo' => 18,
                'contenido' => 'Después de él restauraron sus hermanos, Bavai hijo de Henadad, gobernador de la mitad de la región de Keila.',
                'referencia' => 'Nehemías 3:18',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
