            372 => 
            array (
                'id' => 12374,
                'capitulo_id' => 416,
                'pericopa_id' => 903,
                'num_versiculo' => 19,
                'contenido' => 'Y al lado de él restauró Ezer hijo de Jesúa, gobernador de Mizpa, otro tramo frente a la subida de la armería de la esquina.',
                'referencia' => 'Nehemías 3:19',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
            ),
            373 => 
            array (
                'id' => 12375,
                'capitulo_id' => 416,
                'pericopa_id' => 903,
                'num_versiculo' => 20,
                'contenido' => 'Después de él Baruc hijo de Zabai restauró con todo fervor otro tramo, desde la esquina hasta la puerta de la casa del sumo sacerdote Eliasib.',
                'referencia' => 'Nehemías 3:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
