            374 => 
            array (
                'id' => 12376,
                'capitulo_id' => 416,
                'pericopa_id' => 903,
                'num_versiculo' => 21,
                'contenido' => 'Tras él restauró Meremot hijo de Urías, hijo de Cos, otro tramo, desde la entrada de la casa de Eliasib hasta el extremo de la casa de Eliasib.',
                'referencia' => 'Nehemías 3:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
