            375 => 
            array (
                'id' => 12377,
                'capitulo_id' => 416,
                'pericopa_id' => 903,
                'num_versiculo' => 22,
                'contenido' => 'Y después de él restauraron los sacerdotes, los hombres de la llanura.',
                'referencia' => 'Nehemías 3:22',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:16',
                'updated_at' => '2024-03-12 15:25:16',
