            396 => 
            array (
                'id' => 12898,
                'capitulo_id' => 437,
                'pericopa_id' => 932,
                'num_versiculo' => 1,
                'contenido' => 'Hubo en la tierra de Uz un hombre llamado Job; y era este hombre perfecto y recto, y temeroso de Dios y apartado del mal.',
                'referencia' => 'Job 1:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:17',
                'updated_at' => '2024-03-12 15:25:17',
