            397 => 
            array (
                'id' => 12899,
                'capitulo_id' => 437,
                'pericopa_id' => 932,
                'num_versiculo' => 2,
                'contenido' => 'Y le nacieron siete hijos y tres hijas.',
                'referencia' => 'Job 1:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:17',
                'updated_at' => '2024-03-12 15:25:17',
