            141 => 
            array (
                'id' => 17645,
                'capitulo_id' => 671,
                'pericopa_id' => 1227,
                'num_versiculo' => 6,
                'contenido' => 'antes que el cordón de plata se suelte, y se rompa el tazón de oro, y el cántaro se quiebre junto a la fuente, y la rueda del pozo se rompa;',
                'referencia' => 'Eclesiastés 12:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
