            142 => 
            array (
                'id' => 17646,
                'capitulo_id' => 671,
                'pericopa_id' => 1227,
                'num_versiculo' => 7,
                'contenido' => 'y el polvo vuelva a la tierra, como era, y el espíritu vuelva a Dios, quien lo dio.',
                'referencia' => 'Eclesiastés 12:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            143 => 
            array (
                'id' => 17647,
                'capitulo_id' => 671,
                'pericopa_id' => 1227,
                'num_versiculo' => 8,
                'contenido' => 'Vanidad de vanidades, dijo el Predicador; todo es vanidad.',
                'referencia' => 'Eclesiastés 12:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            144 => 
