            array (
                'id' => 17705,
                'capitulo_id' => 675,
                'pericopa_id' => 1234,
                'num_versiculo' => 7,
                'contenido' => 'Toda tú eres hermosa, amada mía, y en ti no hay mancha.',
                'referencia' => 'Cantares 4:7',
                'imagen' => '',
