            ),
            204 => 
            array (
                'id' => 17708,
                'capitulo_id' => 675,
                'pericopa_id' => 1234,
                'num_versiculo' => 10,
                'contenido' => '¡Cuán hermosos son tus amores, hermana mía, esposa mía! ¡Cuánto mejores que el vino tus amores, y la fragancia de tus ungüentos  mejor que todas las especias aromáticas!',
                'referencia' => 'Cantares 4:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            205 => 
            array (
                'id' => 17709,
                'capitulo_id' => 675,
                'pericopa_id' => 1234,
                'num_versiculo' => 11,
                'contenido' => 'Como panal de miel destilan tus labios, oh esposa; miel y leche hay debajo de tu lengua; y la fragancia de tus vestidos como el olor del Líbano.',
                'referencia' => 'Cantares 4:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            206 => 
            array (
                'id' => 17710,
                'capitulo_id' => 675,
                'pericopa_id' => 1234,
                'num_versiculo' => 12,
                'contenido' => 'Huerto cerrado eres, hermana mía, esposa mía; fuente cerrada, fuente sellada.',
                'referencia' => 'Cantares 4:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
