            array (
                'id' => 17711,
                'capitulo_id' => 675,
                'pericopa_id' => 1234,
                'num_versiculo' => 13,
                'contenido' => 'Tus renuevos paraíso de granados, con frutos exquisitos, de alheña y de nardos,',
                'referencia' => 'Cantares 4:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
