            214 => 
            array (
                'id' => 17718,
                'capitulo_id' => 676,
                'pericopa_id' => 1236,
                'num_versiculo' => 4,
                'contenido' => 'Mi amado metió su mano por la abertura de la puerta, y mis entrañas se conmovieron por él.',
                'referencia' => 'Cantares 5:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
