            array (
                'id' => 17722,
                'capitulo_id' => 676,
                'pericopa_id' => 1236,
                'num_versiculo' => 8,
                'contenido' => 'Yo os ruego, oh hijas de Jerusalén, si halláis a mi amado, hacedle saber que estoy enferma de amor.',
