            96 => 
            array (
                'id' => 18100,
                'capitulo_id' => 696,
                'pericopa_id' => 1268,
                'num_versiculo' => 1,
                'contenido' => 'Profecía sobre Damasco: He aquí que Damasco ha dejado de ser ciudad y será montón de ruina.',
                'referencia' => 'Isaías 17:1',
