            array (
                'id' => 18106,
                'capitulo_id' => 696,
                'pericopa_id' => 1269,
                'num_versiculo' => 7,
                'contenido' => 'En aquel día mirará el hombre a su Hacedor, y sus ojos contemplarán al Santo de Israel.',
                'referencia' => 'Isaías 17:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
