            array (
                'id' => 18116,
                'capitulo_id' => 697,
                'pericopa_id' => 1270,
                'num_versiculo' => 3,
                'contenido' => 'Vosotros, todos los moradores del mundo y habitantes de la tierra, cuando se levante estandarte en los montes, mirad, y cuando se toque trompeta, oíd.',
                'referencia' => 'Isaías 18:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
