            array (
                'id' => 18157,
                'capitulo_id' => 700,
                'pericopa_id' => 1273,
                'num_versiculo' => 6,
                'contenido' => 'porque el Señor me dijo así: Ve, pon centinela que haga saber lo que vea.',
                'referencia' => 'Isaías 21:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
