            154 => 
            array (
                'id' => 18158,
                'capitulo_id' => 700,
                'pericopa_id' => 1273,
                'num_versiculo' => 7,
                'contenido' => 'Y vio hombres montados, parejas de jinetes, gente montada en asnos y gente montada en camellos. Luego miró más atentamente,',
                'referencia' => 'Isaías 21:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
