            155 => 
            array (
                'id' => 18159,
                'capitulo_id' => 700,
                'pericopa_id' => 1273,
                'num_versiculo' => 8,
                'contenido' => 'y gritó como un león: Señor, sobre la atalaya estoy yo continuamente de día, y las noches enteras sobre mi guardia;',
                'referencia' => 'Isaías 21:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
