                'capitulo_id' => 700,
                'pericopa_id' => 1275,
                'num_versiculo' => 14,
                'contenido' => 'Salid a encontrar al sediento; llevadle agua, moradores de la tierra de Tema, socorred con pan al que huye.',
                'referencia' => 'Isaías 21:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
