            162 => 
            array (
                'id' => 18166,
                'capitulo_id' => 700,
                'pericopa_id' => 1275,
                'num_versiculo' => 15,
                'contenido' => 'Porque huyen ante la espada, ante la espada desnuda, ante el arco tensado, ante la violencia de la batalla.',
                'referencia' => 'Isaías 21:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
