            166 => 
            array (
                'id' => 18170,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 2,
                'contenido' => 'Tú, llena de alborotos, ciudad turbulenta, ciudad alegre; tus muertos no son muertos a espada ni muertos en guerra.',
