            167 => 
            array (
                'id' => 18171,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 3,
                'contenido' => 'Todos tus gobernantes huyeron juntos; sin arco fueron capturados; todos los tuyos fueron atados juntos, aunque habían huido lejos.',
                'referencia' => 'Isaías 22:3',
                'imagen' => '',
