            169 => 
            array (
                'id' => 18173,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 5,
                'contenido' => 'Porque día es de alboroto, y de atropello y de confusión, de parte del Señor Jehová de los ejércitos, en el valle de la visión, para derribar el muro y clamar al monte.',
                'referencia' => 'Isaías 22:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
