            ),
            170 => 
            array (
                'id' => 18174,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 6,
                'contenido' => 'Y Elam tomó la aljaba con carros y con jinetes, y Kir sacó el escudo.',
                'referencia' => 'Isaías 22:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
