            171 => 
            array (
                'id' => 18175,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 7,
                'contenido' => 'Y acaeció que tus hermosos valles se llenaron de carros, y los de a caballo se apostaron a la puerta.',
                'referencia' => 'Isaías 22:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
