            177 => 
            array (
                'id' => 18181,
                'capitulo_id' => 701,
                'pericopa_id' => 1276,
                'num_versiculo' => 13,
                'contenido' => 'Y he aquí gozo y alegría, matando vacas y degollando ovejas, comiendo carne y bebiendo vino, diciendo: Comamos y bebamos, porque mañana moriremos.',
                'referencia' => 'Isaías 22:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
