            186 => 
            array (
                'id' => 18190,
                'capitulo_id' => 701,
                'pericopa_id' => 1277,
                'num_versiculo' => 22,
                'contenido' => 'Y pondré la llave de la casa de David sobre su hombro; y abrirá, y nadie cerrará; cerrará, y nadie abrirá.',
                'referencia' => 'Isaías 22:22',
