            296 => 
            array (
                'id' => 18300,
                'capitulo_id' => 707,
                'pericopa_id' => 1284,
                'num_versiculo' => 20,
                'contenido' => 'Porque la cama es corta para estirarse en ella, y la manta estrecha para envolverse en ella.',
                'referencia' => 'Isaías 28:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
