            298 => 
            array (
                'id' => 18302,
                'capitulo_id' => 707,
                'pericopa_id' => 1284,
                'num_versiculo' => 22,
                'contenido' => 'Ahora, pues, no seáis escarnecedores, no sea que se aprieten más vuestras ataduras; porque una destrucción total ya determinada sobre toda la tierra he oído del Señor Jehová de los ejércitos.',
                'referencia' => 'Isaías 28:22',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            299 => 
            array (
                'id' => 18303,
                'capitulo_id' => 707,
                'pericopa_id' => 1284,
                'num_versiculo' => 23,
                'contenido' => 'Estad atentos y oíd mi voz; estad atentos y oíd mis palabras.',
