            300 => 
            array (
                'id' => 18304,
                'capitulo_id' => 707,
                'pericopa_id' => 1284,
                'num_versiculo' => 24,
                'contenido' => '¿Arará todo el día el que ara para sembrar? ¿Romperá y rastrillará su tierra?',
                'referencia' => 'Isaías 28:24',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
