            306 => 
            array (
                'id' => 18310,
                'capitulo_id' => 708,
                'pericopa_id' => 1285,
                'num_versiculo' => 1,
                'contenido' => '¡Ay de Ariel, de Ariel, ciudad donde habitó David! Añadid un año a otro, y que las fiestas sigan su curso.',
                'referencia' => 'Isaías 29:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
            ),
            307 => 
            array (
                'id' => 18311,
                'capitulo_id' => 708,
                'pericopa_id' => 1285,
                'num_versiculo' => 2,
                'contenido' => 'Mas yo pondré a Ariel en aprietos, y habrá desconsuelo y tristeza; y será para mí como Ariel.',
                'referencia' => 'Isaías 29:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:22',
                'updated_at' => '2024-03-12 15:25:22',
