            51 => 
            array (
                'id' => 18555,
                'capitulo_id' => 719,
                'pericopa_id' => 1301,
                'num_versiculo' => 19,
                'contenido' => 'El artífice funde la imagen tallada; el orfebre la recubre de oro y le labra cadenas de plata.',
                'referencia' => 'Isaías 40:19',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
                'updated_at' => '2024-03-12 15:25:23',
