            138 => 
            array (
                'id' => 18642,
                'capitulo_id' => 722,
                'pericopa_id' => 1307,
                'num_versiculo' => 21,
                'contenido' => 'Este pueblo he formado para mí; mis alabanzas publicará.',
                'referencia' => 'Isaías 43:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:23',
