            array (
                'id' => 18677,
                'capitulo_id' => 723,
                'pericopa_id' => 1310,
                'num_versiculo' => 28,
                'contenido' => 'el que dice de Ciro: Es mi pastor y cumplirá todo lo que yo quiero, al decir de Jerusalén: Sea edificada; y del templo: Sea fundado.',
                'referencia' => 'Isaías 44:28',
                'imagen' => '',
