            408 => 
            array (
                'id' => 18912,
                'capitulo_id' => 737,
                'pericopa_id' => 1328,
                'num_versiculo' => 10,
                'contenido' => 'y si extiendes tu alma al hambriento y sacias al alma afligida, en las tinieblas nacerá tu luz, y tu oscuridad será como el mediodía;',
                'referencia' => 'Isaías 58:10',
                'imagen' => '',
